package com.amazon.demo;

public class Client {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		System.out.println(--a);//9
		System.out.println(++a);//10
		System.out.println(a);//10
		System.out.println(a--);//10
		System.out.println(a);//9
		System.out.println(a--);//9
		System.out.println(a++);//8
		System.out.println(--a);//8
		System.out.println(a);//8
		System.out.println(a++);//8
		System.out.println(--a);//8
		System.out.println(a--);//8
		
		

	}

}
